css:
h1 {
  color: #444;
  font-weight: normal;
  font-size: 3em;
  letter-spacing: 1px;
  line-height: 2em;
  margin: 0;
  text-align: center;
  width: 100%;
  text-transform: uppercase;

}

.widget {
    display: flex;
    flex-direction: column;
    min-height: 1px;
    padding: 15px;
    border-radius: var(--border-radius-md);
    height: 100%;
    box-shadow: var(--card-shadow);
    background-color: var(--card-bg);
}
