css:
.print-main-section .right-align {
    text-align:right;
    padding-top:10px;
}
.print-main-section table {
    width:100%;
}
.print-main-section .logo{
    max-width: max-content;
}
.pt-1 {
    padding-top:10px;
}
p {
    font-size:14px;
}
.due-payment {
    width: 45%;
    padding: 5px 15px;
    margin-top: 20px;
    border: 1px solid #dee2e6;
    text-align: left;
    float: right;
}
.due-payment p {
    font-size: 16px;
    font-weight: 500;
}
.due-payment label {
        font-weight: 400;
    margin: 10px 0 5px 0;
    text-align: left;
}
.comments {
    text-align: justify;
}
.custom-comment {
    height: 75px;
    overflow: hidden;
    color: #6c757d!important;
    font-size:13px!important;
}
.description h5 {
    font-weight:700 !important;
}
.job-description {
   margin:30px 0;
   font-size:14px;
}
.job-description label {
        font-weight: normal;
        margin: 7px 0;
        color: #6c757d;
}
.job-description p {
    font-weight:500;
}
.footer-text{
    text-align: right;
    font-size: 16px;
    font-weight: 500;
}

.address label { width: 85%;
}
.print-format td {
   width: 25%;
}
.table>tbody>tr>td, .table>tbody>tr>th, .table>tfoot>tr>td, .table>tfoot>tr>th, .table>thead>tr>td, .table>thead>tr>th {
    font-weight: 500 !important;
    padding: 12px !important;
    text-align: left;
}
