css:
.main-invoice-sec {
    display: flex;
    justify-content: space-between;
    width: 100%;
        margin-bottom: 20px;
}
.main-invoice-sec .right-section .invoice-name {
    font-weight:500;
}
.main-invoice-sec .right-section .due-payment{
    margin: 30px 0;
    font-size: 16px;
}
.main-invoice-sec .right-section .due-payment label{
   color: #8D9CBC;
    font-weight: normal;
}
.main-invoice-sec .payment-list {
        font-size: 13px;
    text-align: left;
}
.main-invoice-sec .payment-list tr td {
      padding: 3px 6px 3px 0 !important;
}
.main-invoice-sec .payment-list tr td label {
      color: #8D9CBC;
    font-weight: normal;
}
.main-invoice-sec .table-list tbody tr th {
    font-weight:500 !important;
}
table{
    text-align:center;
}
th{
    text-align:center;
}
.main-invoice-sec p {
    color:#2A2E43;
    font-size:14px;
}
.table>tbody>tr>th {
  font-weight: 500 !important;
    padding: 12px !important;
    text-align: left;
}
.table>tbody>tr>td {
    text-align:left !important;
    padding: 12px !important;
}

.footer-detail table {
     float:right;
    display: flex;
    font-size: 14px;
}
.footer-detail table tbody {
    background: #ECECF1;
}
.footer-total {
    display: flex;
    float:right;
    padding-top: 10px;
}
.footer-total h4{
        font-weight: 400;
    padding-right: 20px;
}
.main-invoice-sec .left-section .custome_name {
    margin-top:20px;    
}
.left-section {
    width: 70%;
    float: left;
}
.right-section {
        width: 30%;
    float: left;
}
.table-responsive {
    width: 100%;
    margin-bottom: 15px;
    overflow-y: hidden;
    -ms-overflow-style: -ms-autohiding-scrollbar;
    border: none !important;
}
